// this function checks that the current client requesting a message
// is not the only client who has put a message on the server so far
int check_for_multiple_clients(int id) {
	if(past_client == 0) {
		past_client = id;
		count++;
		return -1;
	}

	if(past_client == id && count == 1) {
		return -1;
	}
	else {
		count++;
		return 0;
	}
}


